    allMetrics,
    allMetricsSide,
    lightlevels1,
    humiditylevels1,
    ph1,
    lightlevels2,
    humiditylevels2,
    waterlevels2,
    lightlevels3,
    humiditylevels3,
    waterlevels3,
} from "variables/charts.js";



function HydroStat(props) {
    const { title, data1, data2, data3 } = props;
    return (
        <CardSpecial chart>
            <CardBody
                style={{
                    display: "flex",
                    justifyContent: "center",
                }}>

                <div>
                    <CardHeader>
                        <h4
                            style={{
                                fontWeight: "bold",
                            }}>{title}</h4>
                    </CardHeader>

                    <p>{data1}</p>
                    <p>{data2}</p>
                    <p>{data3}</p>
                </div>


            </CardBody>
        </CardSpecial>
    )
}

