import React from 'react';
import ChartistGraph from 'react-chartist';
import CardSpecial from "../Card/CardSpecial"
import CardBody from "components/Card/CardBody.js";
import CardHeader from "components/Card/CardHeader.js";
import Button from '@material-ui/core/Button';
import View from '@material-ui/core/Button';
import {
    allMetrics,
    allMetricsSide,
    dataPoints,
    toggleConst
} from "variables/charts.js";
import { all } from 'q';



function HydroStat(props) {
    const { title, data1, data2, data3 } = props;
    return (
        <CardSpecial chart>
            <CardBody
                style={{
                    display: "flex",
                    justifyContent: "center",
                }}>

                <div>
                    <CardHeader>
                        <h4
                            style={{
                                fontWeight: "bold",
                            }}>{title}</h4>
                    </CardHeader>

                    <p>{data1}</p>
                    <p>{data2}</p>
                    <p>{data3}</p>
                </div>


            </CardBody>
        </CardSpecial>
    )
}

function toggle() {
    console.log("Toggling")
}

export default function HydroSidebar() {
    var Side = [];
    console.log(allMetrics);
    console.log("done")
    // console.log(allMetrics[10])
    for (var count = 0; count < Math.floor(allMetrics.length / 4); count++) {
        console.log(count)
        // console.log(allMetricsSide)
        console.log(allMetrics[count * 4])
        console.log([allMetricsSide[count], allMetrics[count * 4].data.series[0][dataPoints - 1], allMetrics[count * 4 + 1].data.series[0][dataPoints - 1], allMetrics[count * 4 + 2].data.series[0][dataPoints - 1]])
        Side.push([allMetricsSide[count], allMetrics[count * 4].data.series[0][dataPoints - 1], allMetrics[count * 4 + 1].data.series[0][dataPoints - 1], allMetrics[count * 4 + 2].data.series[0][dataPoints - 1]])
    }
    console.log("Side is")
    console.log(allMetrics.length / 4)
    console.log(Side);
    return (

        <div
            style={{
                backgroundColor: "#43a047",
                border: "none",
                position: "absolute",
                top: "0",
                bottom: "0",
                left: "0",
                width: "15vw",
                height: "100vh",
                overflow: "hidden",
                display: "flex",
                justifyContent: "center",
                flexDirection: "column",
                alignItems: "center"

            }}
        >

            <Toggle />
            {Side.map(i => {
                console.log("I is")
                console.log(i)
                return (<HydroStat title={i[0]} data1={"Light level%: " + i[2]} data2={"Humidity level%: " + i[1]} data3={"PH: " + i[3]} />)
            })};
            {/* <HydroStat title="Aeroponic 1" data1={"Light level%: " + lightlevels1.data.series[0][lightlevels1.data.series[0].length - 1]} data2={"Humidity level%: " + humiditylevels1.data.series[0][humiditylevels1.data.series[0].length - 1]} data3={"PH: " + ph1.data.series[0][ph1.data.series[0].length - 1]} />
            <HydroStat title="Aeroponic 2" data1={"Light level%: " + lightlevels2.data.series[0][lightlevels2.data.series[0].length - 1]} data2={"Humidity level%: " + humiditylevels2.data.series[0][humiditylevels2.data.series[0].length - 1]} data3={"Water level (cm): " + waterlevels2.data.series[0][waterlevels2.data.series[0].length - 1]} />
            <HydroStat title="Aeroponic 3" data1={"Light level%: " + lightlevels3.data.series[0][lightlevels3.data.series[0].length - 1]} data2={"Humidity level%: " + humiditylevels3.data.series[0][humiditylevels3.data.series[0].length - 1]} data3={"Water level (cm): " + waterlevels3.data.series[0][waterlevels3.data.series[0].length - 1]} /> */}

        </div>
    );
}

class Toggle extends React.Component {
    constructor(props) {
        super(props);
        this.state = { isToggleOn: true };

        // This binding is necessary to make `this` work in the callback
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick() {
        Object.assign(toggleConst, !this.state.isToggleOn)
        this.forceUpdate();
        this.setState(prevState => ({
            isToggleOn: !prevState.isToggleOn,
        }));
    }

    render() {
        return (
            <CardSpecial chart>
                <View onClick={this.handleClick} style={{
                    flex: 1, display: "flex",
                    justifyContent: "center",
                }}>
                    {this.state.isToggleOn ? 'Short Term' : 'Long Term'}</View>
            </CardSpecial>

        );
    }
}
